        <>
            <Box sx={{ flexGrow: 1 }}>
                <AppBar position="static">
                    <Toolbar>
                        <Typography
                            variant="h6"
                            component="div"
                            sx={{ cursor: "pointer" }}
                            onClick={() => navigate("/")}
                        >
                            Samarth Tourist Management Service
                        </Typography>
                    </Toolbar>
                </AppBar>
            </Box>
            <br /><br /><br /><br />
            <Grid container align={'center'}>
                <Grid item xs={12}>
                    <MapContainer style={{ height: '70vh', width: '70vw' }}
                        center={[14.8, 77.4]} zoom={5} scrollWheelZoom={true}>
                        <TileLayer {...tileLayer} />
                        <MarkerClusterGroup
                            chunkedLoading
                        >
                            {hotel.map((users1) =>
                                <Marker position={[users1.location.lat, users1.location.long, "abc"]} >
                                    <Tooltip>
                                    Hotel: {users1.name} <br /> Room Available: {users1.room_count}
                                    </Tooltip>
                                </Marker>
                            )}
                        </MarkerClusterGroup>
                    </MapContainer>
                </Grid>
            </Grid>
            <br />
        </>
    );
};

export default Map;
